import React, { useEffect, useState } from "react";
import { useDispatch, useSelector} from 'react-redux';
import { getAllDogs } from '../actions';
import { Link } from 'react-router-dom'
import Card from "./Card";

export default function Home(){
    const dispatch = useDispatch(); // pasar a archivo nuevo

    const allDogs = useSelector((state) => state.dogs) //lo mismo que mapStateToProps - trae todo lo de actions

    useEffect(()=>{
        dispatch(getAllDogs());
    },[])

    function handleClick(event){
        event.preventDefault();
        dispatch(getAllDogs());
    }

    return(
        <div>
            <Link to = '/dogs'>Create a new dog!</Link>
            <h1>My very first dog App</h1>
            <button onClick = {e=>{handleClick(e)}}>
                Reload all dogs
            </button>
            <div> 
                <select>
                    <option value = 'existent breeds'>Api Breeds</option>
                    <option value = 'created breeds'>Created Breeds</option>
                    <option value = 'all breeds'>All Breeds</option>
                    <option value = 'temperament'>Temperament</option>
                </select>
                <select>
                    <option value = 'weight'>Weight</option>
                    <option value = 'ascendent'>Ascendent</option>
                    <option value = 'aescendent'>Descendent</option>
                </select>
                {
                    allDogs && allDogs.map(dog=>{
                        <Card name = {dog.name} 
                              temperament = {dog.temperament} 
                              weight = {dog.weight} 
                              image = {dog.img} 
                        />
                    })
                }
            </div>
        </div>
    )

}
